mapper.select:
SELECT * FROM (
  SELECT @RN:=@RN+1 as r_num,a.* FROM(
SELECT * FROM charging.bbs_t 
WHERE b_status = 0
ORDER BY b_idx DESC
  ) a, (SELECT @RN:=0) as r
) a WHERE a.r_num BETWEEN #{begin} AND #{end}

SELECT * FROM (
  SELECT @RN:=@RN+1 as r_num,a.* FROM(
SELECT * FROM charging.bbs_t
WHERE b_to = 0 AND b_status = 0
ORDER BY b_idx DESC
  ) a, (SELECT @RN:=0) as r
) a WHERE a.r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE charging.bbs_t
SET b_hit = b_hit+1
WHERE b_idx = #{b_idx}
